import React from 'react';
import PropTypes from 'prop-types';
import '../styles.scss';

import useForm from '../../../hooks/useForm';
import validate from '../Validation';
import Input from '../../../components/Form/Input';
import Button from '../../../components/Button';

const SignUp = ({ submitForm }) => {
    const { handleSubmit, handleChange, values, errors } = useForm( submitForm, validate );

    return (
        <form
            className='subscribe-container'
            onSubmit={handleSubmit}
            noValidate
        >
            <div className='subscribe-column'>
                <h1>Never Miss Our Latest News<span> </span><br/>& Offers</h1>
                <p>Присоединяйтесь сейчас, чтобы получить скидку 15% на свой первый заказ и насладиться бесплатным бургером. Подпишитесь на нашу рассылку новостей и получайте приятные награды.</p>
            </div>
            <div className='subscribe-row'>
                <Input
                    type='text'
                    name='email'
                    placeholder='Введите Email адресс'
                    value={values.email}
                    errors={errors.email}
                    onChange={handleChange}
                />
                <Button type='submit' text='Присоедениться'/>
            </div>
        </form>
    )
}

SignUp.propTypes = {
    submitForm: PropTypes.func,
  };
  
SignUp.defaultProps = {
    submitForm: '',
  };

export default SignUp;
